const items = $input.all();
const out = [];
for (const item of items) {
  const j = item.json || {};
  const body = (j.body && typeof j.body === "object") ? { ...j.body } : {};
  const freeText = body.free_text || j.free_text || j.query || j.message || j.text || j.input || j.question || (typeof j === "string" ? j : "");
  out.push({
    json: {
      method: j.method || body.method || j.httpMethod || "POST",
      body: {
        user_id: body.user_id || j.user_id || "",
        free_text: String(freeText || ""),
        raw_transaction: body.raw_transaction || j.raw_transaction || "",
        mock: Boolean(body.mock || j.mock),
        template_id: body.template_id || j.template_id || "",
        template_name: body.template_name || j.template_name || ""
      }
    }
  });
}
return out;

