const out = [];
const seeded = (seed) => {
  let x = seed % 2147483647;
  if (x <= 0) x += 2147483646;
  return () => (x = (x * 16807) % 2147483647) / 2147483647;
};
for (const item of items) {
  const j = item.json || {};
  const body = (j.body && typeof j.body === "object") ? j.body : {};
  const seedBase = String(body.template_id || "") + String(body.user_id || "") + String(body.free_text || "");
  let seed = 0;
  for (let i = 0; i < seedBase.length; i++) seed = (seed * 31 + seedBase.charCodeAt(i)) >>> 0;
  const rnd = seeded(seed || 1);
  const stepMocks = steps.map((s) => ({
    step_index: s.index,
    step_name: s.name,
    description: s.description,
    mock_text: `Mock input for ${s.name}`,
    mock_id: `${s.index}-${Math.floor(rnd() * 1e9)}`,
    timestamp: new Date().toISOString()
  }));
  out.push({
    json: {
      ...j,
      mock: { enabled: true, steps: stepMocks },
      body: { ...body, mock: true }
